import React from 'react'
import DrumPadContainer from './DrumPadContainer'
import BankController from './components/BankController';
import Display from './components/Display';
import PowerController from './components/PowerController';
import VolumeController from './components/VolumeController';

export default function DrumMachine() {
    const stanbyDisplayText = 'PLAY A BEAT...' 
    const displayTextLibrary = {
        'Bank 1': 'BANK A',
        'Bank 2': 'BANK B',
        'volume-ctrl': 'VOLUME'
    }

    return (
        <div id='drum-machine'>
            <DrumPadContainer />
            <div id="controls">
                <Display />
                <VolumeController />
                <BankController />
                <PowerController />
            </div>
        </div>
    )
}
