import React from 'react'
import { useSelector } from 'react-redux';

function extractAudioFiles(folder, library) {
    let audio = {
        0: '', 1: '', 2: '', 3: '', 4: '', 5: '', 6: '', 7: '', 8: ''
    }

    return Object.keys(audio).map((key, index) => require(`${folder}${library[key]}`))
}

export default function DrumPad() {

    const { folder, library } = useSelector(state => state.audioUrl);
    let audioFiles = extractAudioFiles(folder, library);

    function handlePadClick() {
        const elem = document.getElementById('testAudio');
        elem.currentTime = 0;
        elem.play();
    }

    function handleKeyPress(event) {
        console.log(event)
        if(event.key === "q") {
            const elem = document.getElementById('testAudio');
            elem.currentTime = 0;
            elem.play();
        }
    }

    return (
        <div id="drum-pad-container">
            <button type="button" className="pad"  id="Q" onClick={handlePadClick} onKeyPress={handleKeyPress}>Q
                <audio id="testAudio">
                    <source src={audioFiles[0]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="W">W
                <audio id="testAudio">
                    <source src={audioFiles[1]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="E">E
                <audio id="testAudio">
                    <source src={audioFiles[2]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="A">A
                <audio id="testAudio">
                    <source src={audioFiles[3]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="S">S
                <audio id="testAudio">
                    <source src={audioFiles[4]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="D">D
                <audio id="testAudio">
                    <source src={audioFiles[5]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="Z">Z
                <audio id="testAudio">
                    <source src={audioFiles[6]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="X">X
                <audio id="testAudio">
                    <source src={audioFiles[7]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="C">C
                <audio id="testAudio">
                    <source src={audioFiles[8]} type="audio/wav"></source>
                </audio>
            </button>
        </div>
    )
}
