import React, {useEffect} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { updateDisplay } from '../actions/display'
import { audioDispLibrary } from '../utils/constants';
import { drumPadActiveStyle, drumPadNormalStyle } from '../utils/constants';

export default function DrumPad(props) {
    // Local state to activate pad
    const [active, setActive]  = React.useState(false);

    // Redux store to access current audio files & volume
    const dispatch = useDispatch();
    const { libNum, audioFiles } = useSelector(state => state.audioFiles);
    const { volume } = useSelector(state => state.volume)

    function activatePad(id) {
        const elem = document.getElementById(id + 'audio');
        elem.currentTime = 0;
        elem.load();
        elem.volume = Number(volume) / 100;
        elem.play();

        dispatch(updateDisplay(audioDispLibrary[libNum][id]))
        
        setActive(true)
        setTimeout(() => setActive(false), 100)
    }

    function handleClick(event) {
        let id = event.target.id;
        activatePad(id)
    }

    function handleKeyDown(event) {
        const key = event.key;
        const keyCode = props.id.toLowerCase();
        
        if(key === keyCode) {
            let id = key.toUpperCase();
            activatePad(id)
        }
    }

    useEffect( () => {
        window.addEventListener('keydown', handleKeyDown);
    })

    return (
        <button style={active ? drumPadActiveStyle : drumPadNormalStyle} type="button" className="pad"  id={props.id} onMouseDown={handleClick} onKeyDown={handleKeyDown} tabIndex="0">{props.id}
            <audio id={`${props.id}audio`}>
                <source src={audioFiles[props.index]} type="audio/wav"></source>
            </audio>
        </button>
    )
}
