import React from 'react'

export default function DrumPad() {
    const audio = new Audio("")

    const clap = require('./samples/bank_A/clap1.wav');

    function handlePadClick() {
        const elem = document.getElementById('testAudio');
        elem.currentTime = 0;
        elem.play();
    }

    return (
        <div id="drum-pad-container">
            <button type="button" className="pad" onClick={handlePadClick}>
                <audio id="testAudio">
                    <source src={clap} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad"></button>
            <button type="button" className="pad"></button>
            <button type="button" className="pad"></button>
            <button type="button" className="pad"></button>
            <button type="button" className="pad"></button>
            <button type="button" className="pad"></button>
            <button type="button" className="pad"></button>
            <button type="button" className="pad"></button>
        </div>
    )
}
