import React from 'react'
import { useDispatch } from 'react-redux'
import { updateDisplay } from '../actions/display.js'


export default function BankController() {
    
    const dispatch = useDispatch();

    const displayTextLibrary = {
        'bank1': 'BANK A',
        'bank2': 'BANK B'
    }

    function handleClick(event) {
        dispatch(updateDisplay(displayTextLibrary[event.target.id]))

        setTimeout(() => dispatch(updateDisplay('PLAY A BEAT...')), 2000)
    }

    return (
        <div id="bank-container" className="ctrl-container">
            <p id="bank-container-label" className="ctrl-label">SOUND BANK</p>
            <label for="Bank 1" className="bank-btn-label">BANK A
                <input type="radio" className="bank-btn-radio" id="Bank 1" name="sound-bank" value="bank1" onClick={handleClick} />
                <span className="bank-btn"></span>
            </label>
            <label for="Bank 2" className="bank-btn-label">BANK B
                <input type="radio" className="bank-btn-radio" id="Bank 2" name="sound-bank" value="bank2" onClick={() => dispatch(updateDisplay('BANK B'))} />
                <span className="bank-btn"></span>
            </label>
        </div>
    )
}
