import React from 'react'
import DrumPad from './DrumPad'

export default function DrumMachine() { 
    const displayTextLibrary = {
        'Bank A': 'BANK A',
        'Bank B': 'BANK B',
        'volume-ctrl': 'VOLUME'
    }

    const [data, setData] = React.useState({
        displayText: 'PLAY A BEAT...'
    });

    function changeDisplay() {
        setData({
            displayText: 'BANK A'
        })

        setTimeout(restoreText, 2000);

        function restoreText() {
            setData({
                displayText: 'PLAY A BEAT...'
            })
        }

    }

    return (
        <div id='drum-machine'>
            <DrumPad />
            <div id="controls">
                <div id="display-container">
                    <div id="display">
                        <p id="display-text">{data.displayText}</p>
                    </div>
                    <h1 id="machine-name">DM123</h1>
                </div>
                <div id="volume-ctrl-container" className="ctrl-container">
                    <label id="volume-ctrl-label" for="volume-ctrl" className="ctrl-label">VOLUME</label>
                    <input type="range" min="0" max="100" value="50" id="volume-ctrl" />
                </div>
                <div id="bank-container" className="ctrl-container">
                    <p id="bank-container-label" className="ctrl-label">SOUND BANK</p>
                    <label for="Bank 1" className="bank-btn-label">BANK A
                        <input type="radio" className="bank-btn-radio" id="Bank 1" name="sound-bank" value="bank1" onClick={changeDisplay} />
                        <span className="bank-btn"></span>
                    </label>
                    <label for="Bank 2" className="bank-btn-label">BANK B
                        <input type="radio" className="bank-btn-radio" id="Bank 2" name="sound-bank" value="bank2" />
                        <span className="bank-btn"></span>
                    </label>
                </div>
                <div id="power-btn-container">
                    <p id="power-label">POWER</p>
                    <label id="power-label">
                        <input type="checkbox" id="power-btn-checkbox" />
                        <span id="power-btn"></span>
                    </label>
                </div>
            </div>
        </div>
    )
}
