import {React, useState} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { updateDisplay } from '../actions/display'
import { audioDispLibrary } from '../utils/constants';
import { drumPadActiveStyle, drumPadNormalStyle } from '../utils/constants';

export default function DrumPad(props) {
    const [active, setActive]  = React.useState(false);
    const dispatch = useDispatch();
    const {libNum, audioFiles} = useSelector(state => state.audioFiles);

    function handleClick(event) {
        let id = event.target.id;
        const elem = document.getElementById(id + 'audio');
        elem.currentTime = 0;
        elem.load();
        elem.play();

        dispatch(updateDisplay(audioDispLibrary[libNum][event.target.id]))
        
        setActive(true)
        setTimeout(() => setActive(false), 100)
    }

    function handleKeyDown(event) {
        const key = event.key;
        const keyCode = props.id.toLowerCase();
        if(key === keyCode) {
            const elem = document.getElementById(key.toUpperCase() + 'audio');
            elem.currentTime = 0;
            elem.load();
            elem.play();

            dispatch(updateDisplay(audioDispLibrary[libNum][event.key.toUpperCase()]))

            setActive(true)
            setTimeout(() => setActive(false), 100)
        }
    }

    useEffect( () => {
        window.addEventListener('keydown', handleKeyDown);
    })

    return (
        <button style={active ? drumPadActiveStyle : drumPadNormalStyle} type="button" className="pad"  id={props.id} onClick={handleClick} onKeyDown={handleKeyDown} tabIndex="0">{props.id}
            <audio id={`${props.id}audio`}>
                <source src={audioFiles[props.index]} type="audio/wav"></source>
            </audio>
        </button>
    )
}
