import React from 'react'
import { useSelector } from 'react-redux';

function extractAudioFiles(folder, library) {
    const audio = {
        0: require('./samples/electro/CYCdh_ElecK01-ClHat01.wav')
    }
}

export default function DrumPad() {

    const { folder, library } = useSelector(state => state.audioUrl)
    const clap = require('./samples/vinyl/CYCdh_VinylK2-ClHat.wav');
    const clap2 = require(folder + 'CYCdh_ElecK01-ClHat01.wav');

    extractAudioFiles(folder, library)

    function handlePadClick() {
        const elem = document.getElementById('testAudio');
        elem.currentTime = 0;
        elem.play();
    }

    function handleKeyPress(event) {
        console.log(event)
        if(event.key === "q") {
            const elem = document.getElementById('testAudio');
            elem.currentTime = 0;
            elem.play();
        }
    }

    return (
        <div id="drum-pad-container">
            <button type="button" className="pad"  id="Q" onClick={handlePadClick} onKeyPress={handleKeyPress}>Q
                <audio id="testAudio">
                    <source src={clap} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="W">W</button>
            <button type="button" className="pad" id="E">E</button>
            <button type="button" className="pad" id="A">A</button>
            <button type="button" className="pad" id="S">S</button>
            <button type="button" className="pad" id="D">D</button>
            <button type="button" className="pad" id="Z">Z</button>
            <button type="button" className="pad" id="X">X</button>
            <button type="button" className="pad" id="C">C</button>
        </div>
    )
}
