import React from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { updateDisplay } from '../actions/display'
import { audioDispLibrary } from '../utils/constants';
import { drumPadActiveStyle, drumPadNormalStyle } from '../utils/constants';

export default function DrumPad(props) {
    const [active, setActive]  = React.useState(false);
    const dispatch = useDispatch();
    const {libNum, audioFiles} = useSelector(state => state.audioFiles);

    function handleActivate(event) {

        console.log(event)

        if (event.type == 'click') {
            let id = event.target.id;
            const elem = document.getElementById(id + 'audio');
            elem.currentTime = 0;
            elem.load();
            elem.play();
        }
        

        dispatch(updateDisplay(audioDispLibrary[libNum][event.target.id]))
        
        setActive(true)
        setTimeout(() => setActive(false), 100)
    }

    return (
        <button style={active ? drumPadActiveStyle : drumPadNormalStyle} type="button" className="pad"  id={props.id} onClick={handleActivate} tabIndex="0">{props.id}
            <audio id={`${props.id}audio`}>
                <source src={audioFiles[props.index]} type="audio/wav"></source>
            </audio>
        </button>
    )
}
