import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { updateDisplay } from './actions/display'
import { audioDispLibrary } from './utils/constants';
import { drumPadActiveStyle, drumPadNormalStyle } from './utils/constants';
import DrumPad from './components/DrumPad';


export default function DrumPadContainer() {
    const dispatch = useDispatch();
    const {libNum, audioFiles} = useSelector(state => state.audioFiles);

    const [active, setActive] = React.useState({Q: false, W: false, E: false,
                                                A: false, S: false, D: false,
                                                Z: false, X: false, C: false})

    function handlePadClick(event) {
        let id = event.target.id;
        const elem = document.getElementById(id + 'audio');
        elem.currentTime = 0;
        elem.load();
        elem.play();

        dispatch(updateDisplay(audioDispLibrary[libNum][event.target.id]))
        
        let newState = {...active};
        newState[id] = true;
        setActive(newState)

        setTimeout(() => {
            let newState2 = {...active};
            newState2[id] = false;
            setActive(newState2)}, 100)
    }

    // Function to handle key press and play drum pad audio
    function handleKeyPress(event) {
        if(event.key === "q" || event.key === 'w' || event.key === 'e' 
            || event.key === 'a' || event.key === 's' || event.key === 'd'
            || event.key === 'z' || event.key === 'x' || event.key === 'c') {

                
            const elem = document.getElementById(event.key.toUpperCase() + 'audio');
            elem.currentTime = 0;
            elem.load();
            elem.play();

            dispatch(updateDisplay(audioDispLibrary[libNum][event.key.toUpperCase()]))

            const id = event.key.toUpperCase();
            let newState = {...active};
            newState[id] = true;
            setActive(newState)

            setTimeout(() => {
                let newState2 = {...active};
                newState2[id] = false;
                setActive(newState2)}, 100)
        }
    }

    useEffect( () => {
        window.addEventListener('keydown', handleKeyPress);
    })

    return (
        <div id="drum-pad-container">
            <DrumPad id="Q" index="0" />
            <DrumPad id="W" index="1" />
            <DrumPad id="E" index="2" />
            <DrumPad id="A" index="3" />
            <DrumPad id="S" index="4" />
            <DrumPad id="D" index="5" />
            <DrumPad id="Z" index="6" />
            <DrumPad id="X" index="7" />
            <DrumPad id="C" index="8" />
        </div>
    )
}


