import React from 'react'
import DrumPadContainer from './DrumPadContainer'
import BankController from './components/BankController';
import Display from './components/Display';
import PowerController from './components/PowerController';

export default function DrumMachine() {
    const stanbyDisplayText = 'PLAY A BEAT...' 
    const displayTextLibrary = {
        'Bank 1': 'BANK A',
        'Bank 2': 'BANK B',
        'volume-ctrl': 'VOLUME'
    }

    return (
        <div id='drum-machine'>
            <DrumPadContainer />
            <div id="controls">
                <Display />
                <div id="volume-ctrl-container" className="ctrl-container">
                    <label id="volume-ctrl-label" for="volume-ctrl" className="ctrl-label">VOLUME</label>
                    <input type="range" min="0" max="100" value="50" id="volume-ctrl" />
                </div>
                <BankController />
                <PowerController />
            </div>
        </div>
    )
}
