import React from 'react'
import DrumPad from './DrumPad'
import BankController from './components/BankController';
import Display from './components/Display';

export default function DrumMachine() {
    const stanbyDisplayText = 'PLAY A BEAT...' 
    const displayTextLibrary = {
        'Bank 1': 'BANK A',
        'Bank 2': 'BANK B',
        'volume-ctrl': 'VOLUME'
    }

    /*
    const [data, setData] = React.useState({
        displayText: stanbyDisplayText
    });

    function changeDisplay(event) {
        console.log(displayTextLibrary[event.target.id])
        setData({
            displayText: displayTextLibrary[event.target.id]
        })

        setTimeout(restoreText, 2000);

        function restoreText() {
            setData({
                displayText: stanbyDisplayText
            })
        }
    }*/

    return (
        <div id='drum-machine'>
            <DrumPad />
            <div id="controls">
                <Display />
                <div id="volume-ctrl-container" className="ctrl-container">
                    <label id="volume-ctrl-label" for="volume-ctrl" className="ctrl-label">VOLUME</label>
                    <input type="range" min="0" max="100" value="50" id="volume-ctrl" onClick={changeDisplay} />
                </div>
                <BankController />
                <div id="power-btn-container">
                    <p id="power-label">POWER</p>
                    <label id="power-label">
                        <input type="checkbox" id="power-btn-checkbox" />
                        <span id="power-btn"></span>
                    </label>
                </div>
            </div>
        </div>
    )
}
