import React from 'react'
import { useDispatch } from 'react-redux'
import { updateDisplay } from '../actions/display.js'
import { setBankA, setBankB } from '../actions/audioUrl.js'


export default function BankController() {
    
    const dispatch = useDispatch();

    const displayTextLibrary = {
        'Bank 1': 'ELECTRO',
        'Bank 2': 'VINYL'
    }

    function handleClick(event) {
        dispatch(updateDisplay(displayTextLibrary[event.target.id]))

        if (event.target.id === "Bank 1") {
            dispatch(setBankA())
        } else if (event.target.id === "Bank 2") {
            dispatch(setBankB())
        }
        
    }

    return (
        <div id="bank-container" className="ctrl-container">
            <p id="bank-container-label" className="ctrl-label">SOUND BANK</p>
            <label for="Bank 1" className="bank-btn-label">BANK A
                <input type="radio" className="bank-btn-radio" id="Bank 1" name="sound-bank" value="bank1" onClick={handleClick} checked='checked' />
                <span className="bank-btn"></span>
            </label>
            <label for="Bank 2" className="bank-btn-label">BANK B
                <input type="radio" className="bank-btn-radio" id="Bank 2" name="sound-bank" value="bank2" onClick={handleClick} />
                <span className="bank-btn"></span>
            </label>
        </div>
    )
}
