import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { updateDisplay } from './actions/display'
import { audioDispLibrary } from './utils/constants';


export default function DrumPad() {
    const dispatch = useDispatch();
    const {libNum, audioFiles} = useSelector(state => state.audioFiles);

    function handlePadClick(event) {
        const elem = document.getElementById(event.target.id + 'audio');
        elem.currentTime = 0;
        elem.load();
        elem.play();

        dispatch(updateDisplay(audioDispLibrary[libNum][event.target.id]))
    }

    // Detect key press and play drum pad audio
    function handleKeyPress(event) {
        
        if(event.key === "q" || event.key === 'w' || event.key === 'e' 
            || event.key === 'a' || event.key === 's' || event.key === 'd'
            || event.key === 'z' || event.key === 'x' || event.key === 'c') {

                
            const elem = document.getElementById(event.key.toUpperCase() + 'audio');
            elem.currentTime = 0;
            elem.load();
            elem.play();

            dispatch(updateDisplay(audioDispLibrary[libNum][event.key.toUpperCase()]))
        }
    }

    useEffect( () => {
        window.addEventListener('keydown', handleKeyPress);
    })

    return (
        <div id="drum-pad-container">
            <button type="button" className="pad"  id="Q" onClick={handlePadClick} onKeyDown={handleKeyPress} tabIndex="0">Q
                <audio id="Qaudio">
                    <source src={audioFiles[0]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="W" onClick={handlePadClick}>W
                <audio id="Waudio">
                    <source src={audioFiles[1]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="E" onClick={handlePadClick}>E
                <audio id="Eaudio">
                    <source src={audioFiles[2]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="A" onClick={handlePadClick}>A
                <audio id="Aaudio">
                    <source src={audioFiles[3]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="S" onClick={handlePadClick}>S
                <audio id="Saudio">
                    <source src={audioFiles[4]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="D" onClick={handlePadClick}>D
                <audio id="Daudio">
                    <source src={audioFiles[5]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="Z" onClick={handlePadClick}>Z
                <audio id="Zaudio">
                    <source src={audioFiles[6]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="X" onClick={handlePadClick}>X
                <audio id="Xaudio">
                    <source src={audioFiles[7]} type="audio/wav"></source>
                </audio>
            </button>
            <button type="button" className="pad" id="C" onClick={handlePadClick}>C
                <audio id="Caudio">
                    <source src={audioFiles[8]} type="audio/wav"></source>
                </audio>
            </button>
        </div>
    )
}
