const standbyDisplayText = 'PLAY A BEAT...';

const displayTextLibrary = {
    'Bank 1': 'BANK A',
    'Bank 2': 'BANK B',
    'volume-ctrl': 'VOLUME',
    'Q': 'Q',
    'W': 'W',
    'E': 'E',
    'A': 'A',
    'S': 'S',
    'D': 'D',
    'Z': 'Z',
    'X': 'X',
    'C': 'C',
    'standby': standbyDisplayText
}

const displayReducer = (state=standbyDisplayText, action) => {
    switch(state.type) {
        case 'UPDATE_DISP': 
            return action.displayText;
        default:
            return state;
    }
}

export default displayReducer;
